import React, { Component } from "react";
import axios from "axios";
import config from "../config.json";

class UserForm extends Component {
  state = {
    clientDetails: [],
    clients: [],
    handleRicerca: this.props.handleRicerca,
    filiali: [],
  };
  handleSubmit = async (e) => {
    e.preventDefault();
    this.state.handleRicerca({
      nag: this.state.clientDetails.nag,
      branch: this.state.clientDetails.branch,
      nome: this.state.clientDetails.nome,
      dataNascita: this.state.clientDetails.dataNascita,
    });
  };

  handleChange = ({ currentTarget: input }) => {
    const clientDetails = { ...this.state.clientDetails };
    clientDetails[input.name] = input.value;
    this.setState({ clientDetails });
  };

  getFiliali = () => {
    const jwt = localStorage.getItem("TOKEN");
    const conf = {
      headers: {
        Authorization: jwt,
      },
    };
    axios.get(config.apiFilialiEndpoint, conf).then((response) => {
      const filiali = [...this.state.filiali];
      this.setState({ filiali: response.data });
      console.log(response.data);
    });
  };
  createSelectItems() {
    let items = [];
    for (let i = 0; i <= this.props.maxValue; i++) {
      items.push(
        <option key={i} value={i}>
          {i}
        </option>
      );
      //here I will be creating my options dynamically based on
      //what props are currently passed to the parent component
    }
    return items;
  }
  render() {
    const { clientDetails } = this.state;
    return (
      <div className="container-fluid mt-4 mb-4 ">
        <div className="row">
          <form className="form-inline" onSubmit={this.handleSubmit}>
            <div className="col-3 bootstrap-select-wrapper ">
              <Input
                type="select"
                onChange={this.onDropdownSelected}
                label="Multiple Select"
                multiple
              >
                {this.createSelectItems()}
              </Input>
            </div>
            <div className="col-3 ">
              <input
                name="nag"
                value={clientDetails.nag || ""}
                onChange={this.handleChange}
                placeholder="NAG"
                type="text"
                className="form-control "
              />
            </div>
            <div className="col-3 ">
              <input
                value={clientDetails.nome || ""}
                onChange={this.handleChange}
                name="nome"
                placeholder="Nome"
                type="text"
                className="form-control "
              />
            </div>
            <div className="col-3 ">
              <input
                value={clientDetails.dataNascita || ""}
                onChange={this.handleChange}
                type="date"
                name="dataNascita"
                className="form-control "
              />
            </div>
            <div className="col text-center">
              <button className="btn btn-primary mt-2">submit</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default UserForm;
